module.exports = ServiceBus;

function ServiceBus(proxy){

	var services = {};
	
	this.bind = function(serviceName,service){
		services[serviceName] = service;	
	}

	this.exec = function(){
		var name = [].shift.apply(arguments);
		var Serv = services[name];
        Serv.apply(proxy,arguments);
	}

}
